import React from 'react'
import {NavLink} from 'react-router-dom'
import '../static/css/nav.css'
// import '../static/js/nav.js'
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css'
import '../../node_modules/bootstrap/dist/js/bootstrap.min.js'
// import Navbar from 'react-bootstrap/Navbar'
// import NavDropdown from 'react-bootstrap/NavDropdown'
//import { LinkContainer } from 'react-router-bootstrap'

const Navbar = () =>{
	const nav = () =>{
		const line = document.querySelector('#line')
		const item = document.querySelector('nav a')

		function indicator(e){
			line.style.left = e.offsetLeft + 'px'
			line.style.width = e.offsetWidth + 'px'
		}

		item.forEach(link =>{
			link.addEventListener('click', (e)=>{
				indicator(e.target)
			})
		})
	}

	return (
			<div className="container">
				<nav className="navbar navbar-expand-lg navbar-light bg-light">
					<NavLink to="/" className="navbar-brand">Aashish Panthi</NavLink>
					<button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
    					<span className="navbar-toggler-icon"></span>
  					</button>

					<div className="collapse navbar-collapse" id="navbarSupportedContent">
						<div id="line"></div>
						<ul className="ml-auto navbar-nav">
							<li className="nav-item">
								<NavLink exact to="/" activeClassName="activeLink" className="nav-link">Home</NavLink>
							</li>
							<li className="nav-item">
								<NavLink exact to="/about" activeClassName="activeLink" className="nav-link">About</NavLink>
							</li>
							<li className="nav-item">
								<NavLink exact to="/service" activeClassName="activeLink" className="nav-link">Service</NavLink>
							</li>
							<li className="nav-item">
								<NavLink exact to="/contact" activeClassName="activeLink" className="nav-link">Contact</NavLink>
							</li>
						</ul>
					</div>
				</nav>
			</div>
	)
}



export default Navbar
